public class Patternprint {

	/**
	 * @param args  Write a program  that prints a pattern like below.
    		*
    		**
    		***
    		****
    		*****
    		******
	 * 
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i;
        int j;
		for(i=1;i<7;i++) 
		{
		   for(j=0;j<i;j++)
		   {
			   System.out.print(" * ");
		   }
		   
		   System.out.println(" ");
		   
		}	  
		
		   }
		
	}


